mapper.select:
SELECT cno, ccomment, cdate, clike, mid, mname, cip
FROM commentview
WHERE board_no=#{no}

mapper.update:

UPDATE board SET board_del='0' 
WHERE board_no=#{board_no} 
AND mno=(SELECT mno FROM member WHERE mid=#{mid})


UPDATE comment SET cdel='0' 
WHERE board_no=#{board_no} 
AND cno=#{no}
AND mno=(SELECT mno FROM member WHERE mid=#{mid})

UPDATE comment 
SET clike=clike+1
WHERE board_no=#{board_no} AND cno=#{no}
