mapper.select:

	SELECT mname
	FROM member
	WHERE mid=#{id}


SELECT mname, mLoginAttemptCount
FROM member
WHERE mid=#{id} AND mgrade > 4 AND mpw=#{pw}

mapper.update:
UPDATE member 
SET mLoginAttemptCount=mLoginAttemptCount+1
WHERE mid=#{id}

UPDATE member 
SET mLoginCount=1
WHERE mid=#{id}
