mapper.select:

	SELECT mname
	FROM member
	WHERE mid=#{id}


SELECT mname, mLoginAttemptCount
FROM member
WHERE mid=#{id} AND mpw=#{pw}

mapper.update:
UPDATE member 
SET mLoginAttemptCount=mLoginAttemptCount+1
WHERE mid=#{id}

mapper.select:
SELECT mname, mLoginAttemptCount
FROM member
WHERE mid=#{id} AND mpw=#{pw}

mapper.update:
UPDATE member 
SET mLoginAttemptCount=0
WHERE mid=#{id}
